import React from 'react'; // eslint-disable-line no-unused-vars
import { Link } from 'react-router';

const makeLink = (text, path, current) => {
  let className = 'sub_link' + (path === current ? ' selected' : '');
  return (
    <Link className={className} to={path}>
      {text}
    </Link>
  );
};

export { makeLink };
